package com.basic;

public class UnaryOP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=9;
		System.out.println(a++);
		System.out.println(++a);
		System.out.println(a--);
		System.out.println(--a);
		System.out.println(b--);
		System.out.println(++b);
		System.out.println(b++);
		System.out.println(--b);

	}

}
