package com.basic;

public class relationalOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=10;
		int c=20;
		System.out.println(a==b);
		System.out.println(a==c);
		System.out.println(a!=b);
		System.out.println(a!=c);
		System.out.println(a>b);
		System.out.println(b>c);
		System.out.println(c>a);
		System.out.println(a<c);
		System.out.println(a<=c);
		System.out.println(b>=c);

	}

}
